p.height= m.heights[0];
if ( p.divergent ) {
    p.message = "chain is divergent starting at block " + msg.payload.divergent;
    p.isdivergent = true;
    p.isnotdivergent = false;
} else {
    p.message = "chain is not divergent";
    p.isdivergent = false;
    p.isnotdivergent = true;
}
p.peernames = [];
p.hashes = [];
p.previoushashes = [];
p.transactions = [];
p.urls = [];
r.sort(function(a,b) {return (a.peername > b.peername) ? 1 : ((b.peername > a.peername) ? -1 : 0);} );
// loop over peers
for ( var i = 0; i < r.length; ++i) {
    p.peernames.push(r[i].peername);
    p.hashes.push(r[i].payload.stateHash);
    p.previoushashes.push(r[i].payload.previousBlockHash);
    p.urls.push(r[i].url);
    // loop over transactions
    for ( var j = 0; j < r[i].payload.transactions.length; ++j) {
        p.transactions.push({
            txn: j,
            firstpeertxn: (j === 0) ? true : false,
            notfirstpeertxn: (j === 0) ? false : true,
            peer: r[i].peername,
            url: r[i].url,
            chaincodeid: new Buffer(r[i].payload.transactions[j].chaincodeID, 'base64').toString(),
            payload: new Buffer(r[i].payload.transactions[j].payload, 'base64').toString(),
            uuid: r[i].payload.transactions[j].uuid,
            timestamp: (new Date(r[i].payload.transactions[j].timestamp.seconds*1000)).toString(),
            result: r[i].payload.nonHashData.transactionResults[j]
        });
    }
}
return msg;

